INSERT INTO events (id, title, content, start_date, duration) VALUES
(1, 'Цікаві тренінги у рамках події День кар’єри ЄС', 'У рамках події День кар’єри ЄС, що буде відбуватися у Львівському університеті з 18 по 20 жовтня для студентів та усіх зацікавлених організатори також підготували цікаві тренінги за участю компетентних спікерів.', NOW(), '60 MINUTES'),
(2, 'У Львівському університеті презентували роман Даніеля Григорчука «Міф та божевілля» про події Євромайдану', '28 січня 2019 року у Дзеркальній залі Львівського національного університету імені Івана Франка відбулася презентація книги Даніеля Григорчука «Міф та божевілля», в якій автор художньо осмислює події Майдану 2013-14 років у стилі магічного реалізму.', NOW(), '30 MINUTES'),
(3, 'Студенти та викладачі економічного факультету запалили більше 1000 свічок у пам’ять про страшні події Голодомору в Україні', 'З ініціативи Економічного наукового клубу студентів, аспірантів, докторантів і молодих учених на економічному факультеті відбулися заходи зі вшанування пам’яті жертв Голодомору.', NOW(), '150 MINUTES');
